// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.7;

/// @title AttestationRegistry Contract 
/// @author bloXmove
contract AttestationRegistry {

    mapping(address => mapping(bytes32 => bytes32[])) public credentialsOfAddress;

    /// @param holder address - holder of the attestation
    /// @param topic bytes32 - attestation topic
    /// @param verfiableCredentials bytes32 - representation of the verifiable credentials belonging to this holder under a given topic
    function setAttestation(address holder, bytes32 topic, bytes32 verfiableCredentials ) public {
        credentialsOfAddress[holder][topic].push(verfiableCredentials);
    }

    /// @param holder address - holder of the attestation
    /// @param topic bytes32 - attestation topic
    /// @return list of verifiable credentials for the provided owner and topic
    function getAttestation(address holder, bytes32 topic) public view returns (bytes32[] memory) {
        return credentialsOfAddress[holder][topic]; 
    }

}

